<?php	      // Database connection	      include("config.php");		  		  // Function to get list of all movies		  function get_movielist()		  {   		      if(config())			  { 					$query = "SELECT movie_name FROM movies";				$result=mysql_query($query);			    				return $result;			  }		  }		  		 // printing the movie list on the Page (just for testing)		  $temp=get_movielist();		  while($row = mysql_fetch_array($temp))		  { 		  echo $row['movie_name']."<br/>";		  }		  		  ?>		  